import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [stocks, setStocks] = useState({});
  const [news, setNews] = useState([]);

  useEffect(() => {
    fetchStocks();
    fetchNews();
    // Set up intervals
    const stockInterval = setInterval(fetchStocks, 60000);
    const newsInterval = setInterval(fetchNews, 60000);
    return () => {
      clearInterval(stockInterval);
      clearInterval(newsInterval);
    };
  }, []);

  const fetchStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/stocks');
      setStocks(response.data);
    } catch (error) {
      console.error('Error fetching stocks:', error);
    }
  };

  const fetchNews = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/news');
      setNews(response.data);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(price);
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString();
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Stock Prices & News</h1>

        <div className="stocks-container">
          <h2>Stock Prices</h2>
          <div className="stocks-grid">
            {Object.entries(stocks).map(([symbol, data]) => (
              <div key={symbol} className="stock-card">
                <h3>{symbol}</h3>
                <div className="stock-price">{formatPrice(data.price)}</div>
                <div className="stock-time">Updated: {formatTime(data.timestamp)}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="news-container">
          <h2>Latest Apple News</h2>
          <div className="news-list">
            {news.map((item, index) => (
              <div key={index} className="news-card">
                <h3>{item.title}</h3>
                <p>{item.content}</p>
                <div className="news-footer">
                  <a
                    href={item.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="read-more"
                  >
                    Read More
                  </a>
                  <span className="news-timestamp">
                    {item.timestamp ? new Date(item.timestamp).toLocaleString() : 'N/A'}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
